import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
import AuthProvider from './AuthProvider';
import Axios from 'axios';

export default class Admin extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            role: 'Admin',
            redirect: false,
            error: '',
        };
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    componentDidMount() {
        navigator.geolocation.getCurrentPosition(function(position) {
            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
        });
    }
    handleChange(changeObject) {
        this.setState(changeObject)
    }
    handleSubmit(event) {
        event.preventDefault();
        const headers = {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        };
        const data = {
            email: this.state.email,
            password: this.state.password,
            role: 'Admin'
        };
        Axios.post(`http://localhost:5000/admin/authenticate`, data, {
                headers: headers
            })
            .then(res => {
                localStorage.setItem('access_token', res.accessToken)
                this.setState({ redirect: true });
            })
            .catch(error => {
                this.setState({ error: error.response.data.message })
                console.log(error.response.data.message)
            })
    }

    render() {
        const { redirect } = this.state;
        if (redirect) {
            return <Redirect to = "/admin/dashboard" / > ;
        }
        return ( <
            div >
            <
            div className = "login-wrapper fadeInDown" >
            <
            div id = "formContent" > { /* Tabs Titles */ } { /* Icon */ } <
            div className = "fadeIn first" >
            <
            h1 > Sign In < /h1> { /* <img src="../img/lock.jpg" id="icon" alt="User Icon" /> */ } <
            /div>

            { /* Login Form */ } <
            form onSubmit = { this.handleSubmit } > {
                this.state.error ? ( <
                    div class = "alert alert-warning fade show p-2"
                    role = "alert" > { this.state.error } <
                    /div>
                ) : (
                    ''
                )
            }

            <
            input type = "email"
            value = { this.state.name }
            onChange = {
                (e) => this.handleChange({ email: e.target.value }) }
            required id = "login"
            className = "fadeIn email second"
            name = "email"
            placeholder = "login" / >
            <
            input type = "password"
            value = { this.state.name }
            onChange = {
                (e) => this.handleChange({ password: e.target.value }) }
            required id = "password"
            className = "fadeIn password third"
            name = "password"
            placeholder = "password" / >
            <
            input type = "submit"
            className = "fadeIn submit fourth"
            defaultValue = "Log In" / >
            <
            /form> { /* Remind Passowrd */ } <
            div id = "formFooter" >
            <
            a className = "underlineHover"
            href = "#" > Forgot Password ? < /a> <
            /div> <
            /div> <
            /div > <
            /div >

        )
    }
}
