import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
import AuthProvider from './AuthProvider';
import Axios from 'axios';

export default class Admin extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            role: 'Admin',
            redirect: false,
            error: '',
        };
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    componentDidMount() {
        navigator.geolocation.getCurrentPosition(function (position) {
            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
        });
        Axios.get(AuthProvider.endpoint+`/admin/token-verify`, {
            headers: headers
        }).then(res => {
           
        }).catch(error => {
            this.setState({ redirect: true })
        })
    }

    handleChange(changeObject) {
        this.setState(changeObject)
    }

    handleSubmit(event) {
        event.preventDefault();
        const headers = {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        };
        const data = {
            email: this.state.email,
            password: this.state.password,
            role: 'Admin'
        };
        Axios.post(AuthProvider.endpoint+`/admin/authenticate`, data, {
            headers: headers
        })
            .then(res => {
                // console.log(res.data.accessToken)
                localStorage.setItem('access_token', res.data.accessToken)
                this.setState({ redirect: true });
            })
            .catch(error => {
                this.setState({ error: error.response.data.message })
                console.log(error.response)
            })
    }

    render() {
        console.log(this.state.redirect)
        // const { redirect } = ;
        if (this.state.redirect) {
            return <Redirect to="/admin/dashboard" />;
        }
        return (
            <div>
                <div className="login-wrapper fadeInDown">
                    <div id="formContent">

                        <div className="fadeIn first">
                            <h1>Sign In</h1>
                        </div>

                        <form onSubmit={this.handleSubmit}>
                            {this.state.error ? (
                                <div className="alert alert-danger fade show login-show p-2" role="alert" >
                                    {this.state.error}
                                </div>
                            ) : (
                                    ''
                                )}

                            <input type="email" value={this.state.name} onChange={(e) => this.handleChange({ email: e.target.value })} required id="login" className="fadeIn email second" name="email" placeholder="login" />
                            <input type="password" value={this.state.name} onChange={(e) => this.handleChange({ password: e.target.value })} required id="password" className="fadeIn password third" name="password" placeholder="password" />
                            <input type="submit" className="fadeIn submit fourth" defaultValue="Log In" />
                        </form>
                        {/* Remind Passowrd */}
                        <div id="formFooter">
                            <a className="underlineHover" href="#">Forgot Password?</a>
                        </div>
                    </div>
                </div >
            </div >

        )
    }
}
